const assert = require('assert')

describe('Avantrip page', () => {
    it('should have the right title', () => {
        browser.url('https://www.avantrip.com/autos')
        const title = browser.getTitle()
        assert.strictEqual(title, 'Alquiler de autos en oferta, con las promociones exclusivas de Avantrip.com')
    })
})


